import java.util.Scanner;

public class Main {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		Scanner sc = new Scanner(System.in);
		
		int x;
		
		x = sc.nextInt();
		
		if (x < 0) {
			System.out.println("Negativo");
		}
		else {
			System.out.println("Não negativo");
		}
		
		sc.close();
				
		
				
				
		

	}

}
